.app-navbar {
  width: 100vw;
  height: 56px;
  background: linear-gradient(90deg, #4f8cff 0%, #a1e3ff 100%);
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000;
  box-shadow: 0 2px 8px rgba(79,140,255,0.15);
}

.app-navbar-logo {
  display: flex;
  align-items: center;
  gap: 10px;
  margin-left: 32px;
}

.logo-link {
  display: flex;
  align-items: center;
  gap: 10px;
  text-decoration: none;
}

.navbar-logo-img {
  width: 32px;
  height: 32px;
  filter: brightness(0) invert(1); /* Make logo white */
}

.modern-logo {
  height: 36px;
  width: auto;
}

.logo-text {
  font-size: 1.5em;
  color: #fff;
  font-weight: 700;
  margin-left: 8px;
  letter-spacing: 1px;
}

.app-navbar-title {
  font-size: 1.5em;
  color: #fff;
  font-weight: 700;
  letter-spacing: 1px;
  text-shadow: 0 2px 8px #4f8cff22;
}
.app-navbar-actions {
  margin-right: 32px;
}

/* Removed user-menu and welcome-text styles as they are no longer needed */

.app-navbar-logout {
  background: #fff;
  color: #4f8cff;
  border: none;
  border-radius: 8px;
  padding: 8px 22px;
  font-size: 1em;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 2px 8px rgba(79,140,255,0.10);
  transition: all 0.2s ease;
}

.app-navbar-logout:hover {
  background: #f8fbff;
  color: #3a6bbd;
  box-shadow: 0 2px 12px rgba(79,140,255,0.20);
  transform: translateY(-1px);
}

.auth-buttons {
  display: flex;
  gap: 12px;
}

.app-navbar-login, .app-navbar-signup {
  padding: 8px 22px;
  border-radius: 8px;
  font-size: 1em;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  transition: background 0.2s, color 0.2s;
}

.app-navbar-login {
  background: transparent;
  color: #fff;
  border: 1px solid #fff;
}

.app-navbar-login:hover {
  background: rgba(255, 255, 255, 0.1);
}

.app-navbar-signup {
  background: #fff;
  color: #4f8cff;
  border: none;
}

.app-navbar-signup:hover {
  background: #e6f2ff;
}

/* Creative navbar overrides */
.creative-navbar {
  background: linear-gradient(90deg, #4f8cff 0%, #a1e3ff 100%) !important;
}
.creative-navbar-logo {
  gap: 16px !important;
}
.creative-navbar-title {
  color: #fff !important;
  font-size: 1.5em !important;
  font-weight: 700 !important;
  text-shadow: 0 2px 8px #4f8cff22 !important;
}
.creative-navbar-actions {
  margin-right: 32px !important;
}
.creative-navbar-logout {
  background: #fff !important;
  color: #4f8cff !important;
  border: none !important;
  border-radius: 8px !important;
  padding: 8px 22px !important;
  font-size: 1em !important;
  font-weight: 600 !important;
  cursor: pointer !important;
  box-shadow: 0 2px 8px rgba(79,140,255,0.10) !important;
  transition: background 0.2s, color 0.2s !important;
}
.creative-navbar-logout:hover {
  background: #4f8cff !important;
  color: #fff !important;
}

/* Admin back button - modern gradient design */
.admin-back-button {
  display: flex;
  align-items: center;
  gap: 8px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  border: none;
  border-radius: 12px;
  padding: 10px 20px;
  font-size: 0.95em;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 12px rgba(102, 126, 234, 0.3);
  transition: all 0.3s ease;
  letter-spacing: 0.3px;
}

.admin-back-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 16px rgba(102, 126, 234, 0.4);
  background: linear-gradient(135deg, #5568d3 0%, #653a8d 100%);
}

.admin-back-button:active {
  transform: translateY(0);
  box-shadow: 0 2px 8px rgba(102, 126, 234, 0.3);
}

.admin-back-button svg {
  flex-shrink: 0;
  transition: transform 0.3s ease;
}

.admin-back-button:hover svg {
  transform: translateX(-3px);
}

.admin-back-button span {
  white-space: nowrap;
}

/* Responsive adjustments for admin button */
@media (max-width: 768px) {
  .admin-back-button {
    padding: 8px 16px;
    font-size: 0.9em;
  }
  
  .admin-back-button span {
    display: none;
  }
}
