/* Enhanced responsive sidebar styles */

.country-sidebar {
  position: fixed;
  top: 56px; /* Height of navbar */
  right: 0; /* Changed from left to right */
  width: 340px;
  max-width: 100vw;
  height: calc(100vh - 56px);
  background: linear-gradient(180deg, #fafbfd 0%, #ffffff 100%);
  box-shadow: -2px 0 20px rgba(0,0,0,0.08); /* Changed shadow direction */
  display: flex;
  flex-direction: column;
  align-items: stretch;
  padding: 0;
  z-index: 1000;
  transition: all 0.3s ease-in-out;
  border-left: 1px solid #e8edf7; /* Changed from right to left */
  overflow-x: hidden;
  overflow-y: auto;
  scrollbar-width: thin;
  scrollbar-color: #d1d9e6 #f5f7fa;
  color: #000; /* Default text color */
}

.country-sidebar.closed {
  transform: translateX(calc(100% - 28px)); /* Updated for right side placement */
}

.country-sidebar.open {
  transform: translateX(0);
}

.sidebar-toggle-btn {
  position: absolute;
  left: -36px;
  top: 50%;
  transform: translateY(-50%);
  width: 36px;
  height: 80px;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border: none;
  border-radius: 8px 0 0 8px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0;
  box-shadow: -4px 0 12px rgba(102, 126, 234, 0.3);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  z-index: 10;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.sidebar-toggle-btn:hover {
  background: linear-gradient(135deg, #764ba2 0%, #667eea 100%);
  box-shadow: -6px 0 16px rgba(102, 126, 234, 0.5);
  width: 40px;
}

.sidebar-toggle-btn:active {
  transform: translateY(-50%) scale(0.95);
}

.chevron-icon {
  width: 20px;
  height: 20px;
  border-right: 3px solid white;
  border-bottom: 3px solid white;
  transform: rotate(135deg);
  transition: transform 0.3s ease;
  margin-right: -4px;
}

.country-sidebar.closed .chevron-icon {
  transform: rotate(-45deg);
  margin-right: 0;
  margin-left: -4px;
}

.sidebar-header {
  padding: 16px 20px;
  border-bottom: 1px solid #e8edf7;
  text-align: center;
}

.sidebar-header h2 {
  margin: 0;
  color: #000;
  font-size: 18px;
  font-weight: 600;
}

.sidebar-header p {
  margin: 6px 0 0;
  color: #64748b;
  font-size: 14px;
}

.sidebar-search {
  padding: 16px;
}

.search-row {
  display: flex;
  gap: 8px;
}

.sidebar-input {
  flex: 1;
  padding: 10px 12px;
  border: 1px solid #b6d0f7;
  border-radius: 8px;
  font-size: 15px;
  outline: none;
  transition: all 0.2s ease;
  color: #000;
  background: #fff;
}

.sidebar-input:focus {
  border-color: #4f8cff;
  box-shadow: 0 0 0 2px rgba(79, 140, 255, 0.2);
}

.sidebar-btn {
  padding: 10px 14px;
  border: none;
  border-radius: 8px;
  background: #e2e8f0;
  color: #4a5568;
  cursor: pointer;
  font-weight: 500;
  transition: all 0.2s ease;
}

.sidebar-btn:hover {
  background: #cbd5e1;
}

.sidebar-btn.search-button {
  background: #4f8cff;
  color: white;
}

.sidebar-btn.search-button:hover {
  background: #3a7dff;
}

.country-suggestions {
  max-height: 200px;
  overflow-y: auto;
  list-style: none;
  padding: 0;
  margin: 10px 0 0;
  border: 1px solid #e2e8f0;
  border-radius: 8px;
  background: white;
}

.country-suggestions li {
  padding: 10px 14px;
  cursor: pointer;
  transition: all 0.2s ease;
  border-bottom: 1px solid #f0f4f9;
}

.country-suggestions li:last-child {
  border-bottom: none;
}

.country-suggestions li:hover {
  background: #f0f4f9;
}

/* Map form styles */
.map-form {
  padding: 16px;
}

.form-group {
  margin-bottom: 16px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: #000;
}

.form-control {
  width: 100%;
  padding: 12px;
  border: 1px solid #b6d0f7;
  border-radius: 8px;
  font-size: 15px;
  background: #fff;
  color: #000;
  transition: all 0.2s ease;
}

.form-control:focus {
  border-color: #4f8cff;
  outline: none;
  box-shadow: 0 0 0 2px rgba(79, 140, 255, 0.2);
}

.form-actions {
  display: flex;
  gap: 10px;
  margin-top: 20px;
}

.save-btn {
  flex: 2;
  background: #4f8cff;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.save-btn:hover {
  background: #3a7dff;
}

.cancel-btn {
  flex: 1;
  background: #e2e8f0;
  color: #4a5568;
}

.cancel-btn:hover {
  background: #cbd5e1;
}

.sidebar-error {
  margin-top: 16px;
  padding: 10px;
  background: #fce8e8;
  border: 1px solid #f8b4b4;
  border-radius: 6px;
  color: #e53e3e;
  font-size: 14px;
}

/* Maps list styles */
.sidebar-maps-list {
  padding: 16px;
  border-top: 1px solid #e8edf7;
}

.sidebar-maps-list h4 {
  margin: 0 0 12px;
  font-weight: 600;
  color: #000;
}

.maps-container {
  max-height: 350px;
  overflow-y: auto;
  padding-right: 6px;
}

.map-card {
  padding: 12px;
  border: 1px solid #e2e8f0;
  border-radius: 8px;
  background: white;
  margin-bottom: 10px;
  transition: all 0.2s ease;
}

.map-card:hover {
  box-shadow: 0 2px 8px rgba(0,0,0,0.06);
  border-color: #b6d0f7;
}

.map-card-title {
  font-weight: 600;
  color: #000;
  margin-bottom: 6px;
}

.map-card-description {
  font-size: 14px;
  color: #64748b;
  margin-bottom: 6px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.map-card-coordinates {
  font-size: 13px;
  font-family: monospace;
  color: #4a5568;
  margin-bottom: 10px;
}

.map-card-actions {
  display: flex;
  gap: 6px;
}

.map-card-btn {
  padding: 6px 12px;
  border: none;
  border-radius: 6px;
  font-size: 13px;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 4px;
  transition: all 0.2s ease;
}

.map-card-btn:first-child {
  background: #e2e8f0;
  color: #4a5568;
}

.map-card-btn:first-child:hover {
  background: #cbd5e1;
}

.map-card-btn:last-child {
  background: #f0f4f9;
  color: #4f8cff;
}

.map-card-btn:last-child:hover {
  background: #e5edfa;
}

.no-maps-message {
  padding: 20px;
  text-align: center;
  color: #64748b;
  background: #f8fafc;
  border-radius: 8px;
  border: 1px dashed #d1d8e4;
}

.add-map-btn {
  width: 100%;
  padding: 12px;
  background: #4f8cff;
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 600;
  margin-top: 16px;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 8px;
}

.add-map-btn:hover {
  background: #3a7dff;
}

/* Advanced responsive styling */
@media (max-width: 768px) {
  .country-sidebar {
    width: 320px;
  }
  
  .sidebar-header h2 {
    font-size: 16px;
  }
  
  .sidebar-input {
    font-size: 14px;
    padding: 8px 10px;
  }
  
  .sidebar-btn {
    padding: 8px 12px;
  }
}

@media (max-width: 480px) {
  .country-sidebar {
    width: 280px;
  }
  
  .map-card {
    padding: 10px;
  }
  
  .form-control {
    padding: 10px;
    font-size: 14px;
  }
  
  .form-group {
    margin-bottom: 12px;
  }
  
  .sidebar-search {
    padding: 12px;
  }
  
  .search-row {
    gap: 6px;
  }
}

/* Focus styles for accessibility */
.sidebar-input:focus,
.form-control:focus,
.sidebar-btn:focus,
.map-card-btn:focus,
.add-map-btn:focus {
  outline: 2px solid #4f8cff;
  outline-offset: 2px;
}

/* Clear button in search input */
.search-input-clear {
  position: absolute;
  right: 10px;
  top: 50%;
  transform: translateY(-50%);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 22px;
  height: 22px;
  border-radius: 50%;
  background-color: #e2e2e2;
  color: #555;
  border: 1px solid #ccc;
  padding: 0;
  transition: all 0.2s ease;
  z-index: 5;
}

.search-input-clear:hover {
  background-color: #d1d1d1;
  color: #333;
  border-color: #999;
}

.search-input-clear:active {
  background-color: #c2c2c2;
  transform: translateY(-50%) scale(0.95);
}

.search-input-clear:focus {
  outline: 2px solid #4f8cff;
  outline-offset: 2px;
}

/* Selected country pill styling */
.selected-country-pill {
  display: inline-flex;
  align-items: center;
  background-color: #e9f0ff;
  border: 1px solid #c2d8ff;
  border-radius: 16px;
  padding: 4px 12px 4px 16px;
  margin: 10px 0;
  font-size: 14px;
  color: #1e40af;
  transition: all 0.2s ease;
}

.selected-country-pill button {
  background: transparent;
  border: none;
  cursor: pointer;
  margin-left: 8px;
  padding: 2px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #4a5568;
  border-radius: 50%;
  transition: all 0.2s ease;
}

.selected-country-pill button:hover {
  background-color: #d1d5db;
  color: #1f2937;
}

.selected-country-pill button:focus {
  outline: 2px solid #4f8cff;
  outline-offset: 2px;
}
